sys_variable_value.value:
(function execute(inputs, outputs) {

    var Response = false;

    var encaminharParaAuditoria = new Auditoria();
    Response = encaminharParaAuditoria.encaminharParaAuditoria(inputs.parameter_nome, inputs.parameter_solicitacao_de_credito, inputs.parameter_valor_do_credito, inputs.parameter_quantidade_de_parcelas);

    outputs.parameter_response = Response;
    
})(inputs, outputs);

